// 切换商品标签页
const switchTab = (index: number) => {
    console.log('切换标签页:', index, productTabs.value[index]?.name);
    if (activeTabIndex.value === index) return;

    activeTabIndex.value = index;
    currentPage.value = 1; // 重置页码
    loadTabProducts();
};

// 加载当前标签页的商品
const loadTabProducts = async () => {
    if (productLoading.value) return;

    productLoading.value = true;

    try {
        const currentTab = productTabs.value[activeTabIndex.value];
        const params = {
            page: currentPage.value,
            size: 12,
            ...currentTab.params
        };

        const result = await getCateProduct(params);

        currentProducts.value = result.records || [];
    } catch (error) {
        uni.showToast({
            title: '加载商品失败',
            icon: 'none'
        });
        currentProducts.value = [];
    } finally {
        productLoading.value = false;
    }
};

// 刷新商品数据（换一批）
const refreshProducts = async () => {
    if (refreshLoading.value || productLoading.value) return;

    refreshLoading.value = true;

    try {
        // 增加页码来获取不同的商品
        currentPage.value += 1;

        const currentTab = productTabs.value[activeTabIndex.value];
        const params = {
            page: currentPage.value,
            size: 12,
            ...currentTab.params
        };

        const result = await getCateProduct(params);

        if (result.records && result.records.length > 0) {
            currentProducts.value = result.records;
           
        } else {
            // 如果没有更多数据，重置到第一页
            currentPage.value = 1;
            await loadTabProducts();
           
        }
    } catch (error) {
        console.error('刷新商品失败:', error);
        uni.showToast({
            icon: 'none'
        });
    } finally {
        refreshLoading.value = false;
    }
};

