    height: 120rpx;
    border-radius: 60rpx;
    margin-right: 30rpx;
    background-color: #f5f5f5;
    position: relative;

   
}

.user-details {
    flex: 1;
    display: flex;
    flex-direction: column;
    cursor: pointer;

    &:active {
        opacity: 0.7;
    }
}

.user-name {
    font-size: 36rpx;
    font-weight: 500;
    color: #333;
    margin-bottom: 10rpx;
}

.user-phone {
    font-size: 26rpx;
    color: #999;
}

.arrow-area {
    display: flex;
    align-items: center;
    padding: 5rpx;
    cursor: pointer;
    
    &:active {
        opacity: 0.7;
    }
}

.arrow-icon {
