/* 内容区域 */
.index-content {
    padding-top: calc(var(--status-bar-height) + 125rpx);
    background-color: #F5F5F5;
}

/* 搜索框 */
.search-container {
    padding: 20rpx 30rpx;
    
    .search-box {
        display: flex;
        align-items: center;
        height: 70rpx;
        background-color: #fff;
        border-radius: 35rpx;
        padding: 0 30rpx;
        cursor: pointer;
        /* 确保整个区域都可点击 */
        width: 100%;
        box-sizing: border-box;
        
        &:active {
            background-color: #f5f5f5;
        }
        
        .search-icon {
            width: 32rpx;
            height: 32rpx;
            margin-right: 20rpx;
            /* 确保图标不阻挡点击 */
            pointer-events: none;
        }
        
        .search-placeholder {
            flex: 1;
            font-size: 28rpx;
            color: #999;
            /* 确保文字不阻挡点击 */
            pointer-events: none;
        }
    }
}

/* 轮播图 */
.banner-container {
    margin: 0 30rpx 20rpx;
    
    .banner-swiper {
        height: 320rpx;
        border-radius: 20rpx;
        overflow: hidden;
        
        .banner-image {
            width: 100%;
            height: 100%;
        }
    }
}

/* 消息公告 */
.notice-container {
    margin: 0 30rpx 20rpx;
    
    .notice-content {
        display: flex;
        align-items: center;
        height: 70rpx;
        background-color: #fff;
        border-radius: 35rpx;
        padding: 0 30rpx;
        width: 100%;
        box-sizing: border-box;
        
        .notice-icon {
            width: 32rpx;
            height: 32rpx;
            margin-right: 20rpx;
            flex-shrink: 0;
        }
        
        .notice-text-wrapper {
            flex: 1;
            height: 70rpx;
            overflow: hidden;
            position: relative;
            min-width: 0; // 确保flex子项可以收缩
            
            .notice-text-slider {
                transition: transform 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94);
                will-change: transform;
                width: 100%;
                
                .notice-text-item {
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    height: 70rpx;
                    cursor: pointer;
                    width: 100%;
                    box-sizing: border-box;
                    
                    &:active {
                        opacity: 0.7;
                    }
                    
                    .notice-text {
                        flex: 1;
                        font-size: 26rpx;
                        color: #666;
                        white-space: nowrap;
                        overflow: hidden;
                        text-overflow: ellipsis;
                        line-height: 70rpx;
                        min-width: 0;
                    }
                    
                    .notice-arrow {
                        width: 24rpx;
                        height: 24rpx;
                        margin-left: 15rpx;
                        flex-shrink: 0;
                        opacity: 0.6;
                    }
                }
            }
        }
    }
}

/* 营销板块 */
.marketing-section {
    margin: 0 30rpx 20rpx;
    display: flex;
    gap: 20rpx;
    height: 320rpx;
    overflow: hidden;
    position: relative;
    z-index: 1;
    
    /* 左侧秒杀区域 */
    .seckill-card {
        flex: 1;
        position: relative;
        border-radius: 20rpx;
        overflow: hidden;
        background: linear-gradient(135deg, #ffeaa7, #fab1a0);
        
        .seckill-bg {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 1;
        }
        
        .seckill-content {
            position: relative;
            z-index: 2;
            padding: 24rpx;
            height: 100%;
            display: flex;
            flex-direction: column;
            
            .seckill-header {
                display: flex;
                justify-content: space-between;
                align-items: flex-start;
                margin-bottom: 20rpx;
                
                .seckill-title-wrapper {
                    display: flex;
                    align-items: baseline;
                    gap: 12rpx;
                    
                    .seckill-main-title {
                        font-size: 45rpx;
                        font-weight: bold;
                        color: #2d3436;
                        line-height: 1;
                        white-space: nowrap;
                    }
                    
                    .seckill-sub-title {
                        font-size: 24rpx;
                        color: #636e72;
                        font-weight: 500;
                        white-space: nowrap;
                    }
                }
                
                .seckill-countdown {
                    display: flex;
                    align-items: center;
                    gap: 6rpx;
                    margin-left: 25rpx;
                    
                    .countdown-item {
                        background-color: #FF2A3E;
                        color: #fff;
                        font-size: 24rpx;
                        font-weight: bold;
                        width: 44rpx;
                        height: 44rpx;
                        border-radius: 8rpx;
                        display: flex;
                        align-items: center;
                        justify-content: center;
                    }
                    
                    .countdown-colon {
                        color: #2d3436;
                        font-size: 24rpx;
                        font-weight: bold;
                    }
                    
                    .countdown-enter-icon {
                        width: 32rpx;
                        height: 32rpx;
                        margin-left: -5rpx;
                    }
                }
            }
            
            .seckill-products {
                display: flex;
                gap: 20rpx;
                flex: 1;
                align-items: flex-end;
                
                .seckill-product-item {
                    flex-shrink: 0;
                    
                    .product-image {
                        width: 120rpx;
                        height: 120rpx;
                        border-radius: 16rpx;
                        margin-bottom: 12rpx;
                        background: #fff;
                        box-shadow: 0 4rpx 8rpx rgba(0, 0, 0, 0.1);
                    }
                    
                    .product-price {
                        display: flex;
                        align-items: center;
                        gap: 8rpx;
                        
                        .current-price {
                            background-color: #000000;
                            color: #fff;
                            font-size: 28rpx;
                            font-weight: bold;
                            padding: 4rpx 12rpx;
                            border-radius: 20rpx;
                        }
                        
                        .original-price {
                            font-size: 22rpx;
                            color: #636e72;
                            text-decoration: line-through;
                        }
                    }
                }
            }
        }
    }
    
    /* 右侧区域 */
    .right-section {
        flex: 1;
        display: flex;
        flex-direction: column;
        gap: 20rpx;
        
        /* 限时折扣卡片 */
        .discount-card {
            height: 160rpx;
            border-radius: 20rpx;
            position: relative;
            overflow: hidden;
            
            .discount-bg-image {
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                z-index: 1;
            }
            
            .discount-overlay {
                position: relative;
                z-index: 2;
                height: 100%;
                padding: 24rpx;
                display: flex;
                flex-direction: column;
                justify-content: space-between;
                
                .discount-text-area {
                    .discount-title {
                        font-size: 36rpx;
                        font-weight: bold;
                        color: #000000;
                        line-height: 1.2;
                        margin-bottom: 8rpx;
                    }
                    
                    .discount-subtitle {
                        font-size: 24rpx;
                        color: #C5A27F;
                        opacity: 0.9;
                        line-height: 1.2;
                        position: relative;
                        top: 40rpx;
                        left: -140rpx;
                    }
                }
                
                .go-button {
                    align-self: flex-end;
                    
                    .go-icon {
                        width: 200rpx;
                        height: 128rpx;
                        position: relative;
                        left: 35rpx;
                    }
                }
            }
        }
        
        /* 优惠券和签到容器 */
        .bonus-container {
            display: flex;
            gap: 20rpx;
            height: 140rpx;
            
            .bonus-card {
                flex: 1;
                border-radius: 20rpx;
                position: relative;
                overflow: hidden;
                
                .bonus-bg-image {
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                }
                
                .bonus-overlay {
                    position: relative;
                    z-index: 2;
                    height: 100%;
                    padding: 16rpx;
                    
                    .bonus-tag {
                        position: absolute;
                        top: 0;
                        left: 0;
                        font-size: 20rpx;
                        font-weight: 500;
                        padding: 8rpx 20rpx;
                        border-radius: 20rpx 0 20rpx 0;
                        color: #fff;
                    }
                    
                    .bonus-main-text {
                        position: absolute;
                        bottom: 35rpx;
                        left: 12rpx;
                        font-size: 30rpx;
                        font-weight: 700;
                        text-shadow: none;
                    }
                }
                
                &.coupon-card {
                    .bonus-tag {
                        background: linear-gradient(135deg, #FF6B8A, #FF8A80);
                        box-shadow: 0 2rpx 8rpx rgba(255, 107, 138, 0.3);
                    }
                    
                    .bonus-main-text {
                        color: #E74C3C;
                    }
                }
                
                &.checkin-card {
                    .bonus-tag {
                        background: linear-gradient(135deg, #4FC3F7, #29B6F6);
                        box-shadow: 0 2rpx 8rpx rgba(79, 195, 247, 0.3);
                    }
                    
                    .bonus-main-text {
                        color: #2196F3;
                    }
                }
            }
        }
    }
}

/* 金刚区导航 */
.nav-grid-container {
    margin: 0 30rpx 30rpx;
    background-color: #fff;
    border-radius: 20rpx;
    overflow: hidden;
    box-shadow: 0 2rpx 12rpx rgba(0, 0, 0, 0.08);
    position: relative;
    z-index: 100;

    .nav-scroll-view {
        width: 100%;
        white-space: nowrap;
        /* 优化滚动性能 */
        -webkit-overflow-scrolling: touch;
        scroll-behavior: smooth;
        position: relative;
        z-index: 1;

        .nav-grid {
            display: flex;
            flex-wrap: nowrap; /* 不换行，支持水平滚动 */
            width: max-content; /* 宽度适应内容 */
            min-height: 320rpx;
            box-sizing: border-box;
            padding: 20rpx 0;
            /* 添加过渡动画 */
            transition: transform 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
            position: relative;
            z-index: 2;

            /* 每页显示8个项目的容器 */
            .nav-page {
                width: 660rpx; /* 调整宽度，留出边距 */
                margin: 0 15rpx; /* 页面间距 */
                display: flex;
                flex-wrap: wrap;
                flex-shrink: 0;
                border-radius: 12rpx;
                /* 添加页面过渡效果 */
                transition: all 0.3s ease;
                position: relative;
                z-index: 3;

                .nav-grid-item {
                    width: 25%;
                    height: 160rpx;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;
                    text-align: center;
                    padding: 10rpx;
                    box-sizing: border-box;
                    /* 添加点击效果 */
                    transition: all 0.2s ease;
                    border-radius: 8rpx;
                    position: relative;
                    z-index: 10;
                    cursor: pointer;

                    &:active {
                        transform: scale(0.95);
                        background-color: rgba(0, 0, 0, 0.05);
                    }

                    .nav-grid-icon-wrapper {
                        width: 90rpx;
                        height: 90rpx;
                        border-radius: 50%;
                        display: flex;
                        align-items: center;
                        justify-content: center;
                        margin-bottom: 8rpx;
                        box-shadow: 0 2rpx 8rpx rgba(0, 0, 0, 0.08);
                        transition: all 0.3s ease;

                        .nav-grid-icon {
                            transition: transform 0.3s ease;
                            width: 60px;
                            height: 60px;
                        }
                    }

                    .nav-grid-text {
                        font-size: 22rpx;
                        color: #333;
                        line-height: 1.3;
                        text-align: center;
                        word-break: break-all;
                        margin-top: 8rpx;
                        font-weight: 500;
                        transition: color 0.3s ease;
                    }

                    /* 点击效果优化 */
                    &:active {
                        .nav-grid-icon-wrapper {
                            transform: scale(1.1);
                            box-shadow: 0 4rpx 16rpx rgba(0, 0, 0, 0.15);

                            .nav-grid-icon {
                                transform: scale(1.1);
                            }
                        }

                        .nav-grid-text {
                            color: #3A41BC;
                        }
                    }
            }
        }
    }

    /* 滚动指示器 */
    .nav-indicators {
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 15rpx 0 10rpx;
        gap: 12rpx;

        .indicator-dot {
            width: 12rpx;
            height: 12rpx;
            border-radius: 50%;
            background-color: #e0e0e0;
            transition: all 0.3s ease;

            &.active {
                background-color: #3A41BC;
                transform: scale(1.2);
            }
        }
    }
}

/* 推荐商品标题 */
.recommend-title {
    display: flex;
    align-items: center;
    height: 80rpx;
    padding: 0 30rpx;
    margin-bottom: 20rpx;
    
    .title-text {
        font-size: 32rpx;
        font-weight: bold;
        color: #333;
        position: relative;
        
        &::before {
