<script lang="ts" setup>
import recommend from "@/components/recommend/index.vue";
import decorate from "./src/decorate/index.vue";
import { useUserStore } from "@/store/user";
import { computed, ref } from "vue";
import { onLoad, onShow } from "@dcloudio/uni-app";
import type { UserItem } from "@/types/user/user";
import type { OrderNumItem } from "@/types/user/order";
import { getOrderNum } from "@/api/user/order";
import { getMemberDecorate } from "@/api/home/home";
import { historyProductList } from "@/api/user/historyProduct";
import { isMerchant } from "@/utils";
import { handleLogin } from "@/utils/request";
import { useScrollTop } from "@/hooks";
import { imageFormat } from "@/utils/format";

const { scrollTop } = useScrollTop();

const userStore = useUserStore();

const layoutRef = ref();

const member = computed({
    get() {
        return userStore.userInfo;
    },
    set(val) {
        return userStore.setUserInfo(val);
    }
});

const count = ref({
    collectCount: 0,
    collectStoreCount: 0,
    stayCommentOrder: 0,
    rePay: 0,
    reReceive: 0,
    returnCount: 0,
    bonus: 0
});

const orderNum = ref<OrderNumItem>({} as OrderNumItem);
const historyCount = ref(0);

const __getOrderNum = async () => {
    try {
        const result = await getOrderNum();
        orderNum.value = result;
    } catch (error) {
        console.error(error);
    }
};

const __getHistoryCount = async () => {
    try {
        const result = await historyProductList();
        historyCount.value = result.length;
    } catch (error) {
        console.error(error);
        historyCount.value = 0;
    }
};

const goPages = (url: string) => {
    uni.navigateTo({
        url
    });
};

// 根据会员等级名称获取对应的背景图片
const getMemberCardBg = (rankName: string) => {
    const rankBgMap: { [key: string]: string } = {
        '黄金会员': '/static/images/member/golds.png',
        '铂金会员': '/static/images/member/platinum.png',
        '黑金会员': '/static/images/member/black_gold.png',
        '钻石会员': '/static/images/member/diamond.png',
        '至尊会员': '/static/images/member/supreme.png'
    };
    return rankBgMap[rankName] || '/static/images/member/golds.png';
};

const resetUser = () => {
    member.value = {} as UserItem;
    count.value = {
        collectCount: 0,
        collectStoreCount: 0,
        stayCommentOrder: 0,
        rePay: 0,
        reReceive: 0,
        returnCount: 0,
        bonus: 0
    };
    orderNum.value = {} as OrderNumItem;
};

const loginSuccess = () => {
    __getMemberDecorate();
    __getOrderNum();
};

const hasToken = computed(() => {
    return userStore.token || uni.getStorageSync("token");
});



const modules = ref<any[]>([]);

const __getMemberDecorate = async () => {
    try {
        const result = await getMemberDecorate();
        modules.value = result.data;
    } catch (error) {
        console.error(error);
    }
};

onLoad(() => {
    __getMemberDecorate();
});

onShow(() => {
    if (!hasToken.value) {
        resetUser();
        return;
    }
    userStore.getUserInfo();
    __getOrderNum();
    __getHistoryCount();
});
</script>
